import React, { useEffect, useState, useRef } from 'react';

const Home = () => {
  const [projects, setProjects] = useState([]);
  const fadeSections = useRef([]);

  useEffect(() => {
    fetch('/projects.json')
      .then(response => {
        if (!response.ok) throw new Error('Network response was not ok');
        return response.json();
      })
      .then(data => setProjects(data.projects))
      .catch(err => console.error('Error fetching projects data:', err));
  }, []);

  useEffect(() => {
    const observer = new IntersectionObserver(entries => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('visible');
          observer.unobserve(entry.target);
        }
      });
    }, { threshold: 0.3 });

    fadeSections.current.forEach(section => {
      if (section) observer.observe(section);
    });

    return () => observer.disconnect();
  }, [projects]);

  const featuredProjects = projects;
  const trendingProjects = projects.filter(p => p.tags?.includes('popular'));

  return (
    <html lang="en">
      <head>
        <meta charSet="UTF-8" />
        <meta name="viewport" content="width=device-width, initial-scale=1.0" />
        <title>YourNextStory - Main</title>
        <link rel="stylesheet" href="/style.css" />
      </head>
      <body>
        <header>
          <h1>YourNextStory</h1>
          <nav role="navigation">
            <ul>
              <li><a className="active" href="main.html">Main</a></li>
              <li><a href="recommendation.html">Recommendations</a></li>
              <li><a href="search.html">Search</a></li>
              <li><a href="profile.html">Profile</a></li>
            </ul>
          </nav>
        </header>

        <main role="main">
          <section aria-labelledby="featured-projects">
            <h2 id="featured-projects">Featured Projects</h2>
            <div id="projects-container" className="scroll-container">
              {featuredProjects.map(project => (
                <figure key={project.title} className="poster-card">
                  <a href={project.link}>
                    <img
                      src={project.poster}
                      alt={`${project.title} Poster`}
                      className="poster"
                    />
                  </a>
                  <figcaption>
                    <a href={project.link}>{project.title}</a>
                  </figcaption>
                </figure>
              ))}
            </div>
          </section>

          <section
            id="trending-media"
            className="fade-in-section"
            ref={el => fadeSections.current[0] = el}
          >
            <h2>Trending Media</h2>
            <div id="trending-media-container">
              {trendingProjects.map(project => (
                <figure key={project.title} className="trending-figure">
                  <a href={project.link}>
                    <img
                      src={project.poster}
                      alt={`${project.title} Poster`}
                      className="trending-poster"
                    />
                  </a>
                  <figcaption>{project.title}</figcaption>
                </figure>
              ))}
            </div>
          </section>
        </main>
      </body>
    </html>
  );
};

export default Home;
